choose.when:
SELECT * FROM ( SELECT T.*, ROW_NUMBER() OVER (
ORDER BY LIKE_COUNT DESC, CREATED_DATE DESC
ORDER BY CREATED_DATE DESC
) AS RN FROM ( SELECT F.FEED_NO, F.CONTENT, F.TAG, F.LATITUDE, F.LONGITUDE, F.LOCATION_NAME, F.CREATED_DATE, F.IS_DEL, F.MEMBER_NO, M.MEMBER_NICK, M.STATUS AS MEMBER_STATUS, NVL(LIKE_CNT.LIKE_COUNT, 0) AS LIKE_COUNT, NVL(COMMENT_CNT.COMMENT_COUNT, 0) AS COMMENT_COUNT
, CASE WHEN FL.MEMBER_NO IS NOT NULL THEN 1 ELSE 0 END AS IS_LIKED
FROM TB_FEED F LEFT JOIN TB_MEMBER M ON F.MEMBER_NO = M.MEMBER_NO LEFT JOIN ( SELECT FEED_NO, COUNT(*) AS LIKE_COUNT FROM TB_FEED_LIKE GROUP BY FEED_NO ) LIKE_CNT ON F.FEED_NO = LIKE_CNT.FEED_NO LEFT JOIN ( SELECT FEED_NO, COUNT(*) AS COMMENT_COUNT FROM TB_COMMENT WHERE IS_DEL = 'N' GROUP BY FEED_NO ) COMMENT_CNT ON F.FEED_NO = COMMENT_CNT.FEED_NO
LEFT JOIN TB_FEED_LIKE FL ON F.FEED_NO = FL.FEED_NO AND FL.MEMBER_NO = #{memberNo}
LEFT JOIN TB_MEMBER_HOME MH_CHECK ON F.MEMBER_NO = MH_CHECK.MEMBER_NO WHERE F.IS_DEL = 'N'
AND MH_CHECK.IS_PRIVATE_PROFILE = 'N'
AND ( MH_CHECK.IS_PRIVATE_PROFILE = 'N' OR F.MEMBER_NO = #{memberNo} OR ( MH_CHECK.IS_PRIVATE_PROFILE = 'Y' AND EXISTS ( SELECT 1 FROM TB_FOLLOWS FLW WHERE FLW.MEMBER_NO = #{memberNo} AND FLW.HOME_NO = MH_CHECK.HOME_NO AND FLW.STATUS = 'Y' ) ) )
AND F.MEMBER_NO = #{targetMemberNo}
AND F.MEMBER_NO IN ( SELECT MH.MEMBER_NO FROM TB_FOLLOWS FLW JOIN TB_MEMBER_HOME MH ON FLW.HOME_NO = MH.HOME_NO WHERE FLW.MEMBER_NO = #{memberNo} )
) T ) WHERE RN BETWEEN #{startRow} AND #{endRow}

mapper.insert:
INSERT INTO TB_FEED_LIKE (
	FEED_NO,
	MEMBER_NO
) VALUES (
	#{feedNo},
	#{memberNo}
)

mapper.delete:
DELETE FROM TB_FEED_LIKE
WHERE FEED_NO = #{feedNo}
AND MEMBER_NO = #{memberNo}

mapper.select:
SELECT COUNT(*)
FROM TB_FEED_LIKE
WHERE FEED_NO = #{feedNo}
AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE TB_FEED
SET IS_DEL = 'Y'
WHERE FEED_NO = #{feedNo}

mapper.insert:
INSERT INTO TB_FEED_BOOKMARK (
	BOOKMARK_NO,
	FEED_NO,
	MEMBER_NO
) VALUES (
	SEQ_BOOKMARK_NO.NEXTVAL,
	#{feedNo},
	#{memberNo}
)

mapper.delete:
DELETE FROM TB_FEED_BOOKMARK
WHERE FEED_NO = #{feedNo}
AND MEMBER_NO = #{memberNo}

mapper.select:
SELECT COUNT(*)
FROM TB_FEED_BOOKMARK
WHERE FEED_NO = #{feedNo}
AND MEMBER_NO = #{memberNo}

SELECT F.FEED_NO, F.CONTENT, F.TAG, F.LATITUDE, F.LONGITUDE, F.LOCATION_NAME, F.CREATED_DATE, F.IS_DEL, F.MEMBER_NO, M.MEMBER_NICK, (SELECT COUNT(*) FROM TB_FEED_LIKE WHERE FEED_NO = F.FEED_NO) AS LIKE_COUNT, (SELECT COUNT(*) FROM TB_COMMENT WHERE FEED_NO = F.FEED_NO AND IS_DEL = 'N') AS COMMENT_COUNT, 1 AS IS_LIKED FROM TB_FEED F JOIN TB_FEED_LIKE FL ON F.FEED_NO = FL.FEED_NO LEFT JOIN TB_MEMBER M ON F.MEMBER_NO = M.MEMBER_NO WHERE FL.MEMBER_NO = #{memberNo} AND F.IS_DEL = 'N'
AND TO_CHAR(F.CREATED_DATE, 'YYYY-MM-DD') >= #{startDate}
AND TO_CHAR(F.CREATED_DATE, 'YYYY-MM-DD') <= #{endDate}
ORDER BY F.CREATED_DATE ASC
ORDER BY F.CREATED_DATE DESC

SELECT C.COMMENT_NO AS commentNo, C.CONTENT AS content, C.CREATE_DATE AS createDate, C.FEED_NO AS feedNo, C.WRITER AS writer, M.MEMBER_NICK AS writerNick, MH.PROFILE_CHANGE_NAME AS writerProfileImage, (SELECT CHANGE_NAME FROM TB_FEED_FILE WHERE FEED_NO = F.FEED_NO AND ROWNUM = 1) AS feedImage FROM TB_COMMENT C JOIN TB_FEED F ON C.FEED_NO = F.FEED_NO JOIN TB_MEMBER M ON C.WRITER = M.MEMBER_NO LEFT JOIN TB_MEMBER_HOME MH ON M.MEMBER_NO = MH.MEMBER_NO WHERE C.WRITER = #{memberNo} AND C.IS_DEL = 'N' AND F.IS_DEL = 'N'
AND TO_CHAR(C.CREATE_DATE, 'YYYY-MM-DD') >= #{startDate}
AND TO_CHAR(C.CREATE_DATE, 'YYYY-MM-DD') <= #{endDate}
ORDER BY C.CREATE_DATE ASC
ORDER BY C.CREATE_DATE DESC
