mapper.select:
SELECT MEMBER_ID
  FROM TB_MEMBER
 WHERE MEMBER_NAME = #{memberName}
   AND EMAIL = #{email}
   AND STATUS = 'Y'

SELECT COUNT(*)
  FROM TB_MEMBER
 WHERE MEMBER_ID = #{memberId}
   AND EMAIL = #{email}
   AND STATUS = 'Y'

mapper.update:
UPDATE TB_MEMBER
   SET MEMBER_PWD = #{memberPwd}
 WHERE MEMBER_ID = #{memberId}
   AND STATUS = 'Y'
