mapper.select:
SELECT H.HOME_NO, H.HOME_TITLE, H.STATUS_MSG, H.PROFILE_ORIGIN_NAME, H.PROFILE_CHANGE_NAME, H.IS_PRIVATE_PROFILE, H.IS_PRIVATE_VISIT, H.IS_PRIVATE_FOLLOW, H.MEMBER_NO, M.MEMBER_NICK, M.MEMBER_NAME, M.STATUS, NVL(FEED_CNT.FEED_COUNT, 0) AS FEED_COUNT, NVL(FOLLOWER_CNT.FOLLOWER_COUNT, 0) AS FOLLOWER_COUNT, NVL(FOLLOWING_CNT.FOLLOWING_COUNT, 0) AS FOLLOWING_COUNT
, CASE WHEN FLW.MEMBER_NO IS NOT NULL THEN 1 ELSE 0 END AS IS_FOLLOWING , FLW.STATUS AS FOLLOW_STATUS
FROM TB_MEMBER_HOME H LEFT JOIN TB_MEMBER M ON H.MEMBER_NO = M.MEMBER_NO LEFT JOIN ( SELECT MEMBER_NO, COUNT(*) AS FEED_COUNT FROM TB_FEED WHERE IS_DEL = 'N' GROUP BY MEMBER_NO ) FEED_CNT ON H.MEMBER_NO = FEED_CNT.MEMBER_NO LEFT JOIN ( SELECT HOME_NO, COUNT(*) AS FOLLOWER_COUNT FROM TB_FOLLOWS WHERE STATUS = 'Y' GROUP BY HOME_NO ) FOLLOWER_CNT ON H.HOME_NO = FOLLOWER_CNT.HOME_NO LEFT JOIN ( SELECT MEMBER_NO, COUNT(*) AS FOLLOWING_COUNT FROM TB_FOLLOWS WHERE STATUS = 'Y' GROUP BY MEMBER_NO ) FOLLOWING_CNT ON H.MEMBER_NO = FOLLOWING_CNT.MEMBER_NO
LEFT JOIN TB_FOLLOWS FLW ON H.HOME_NO = FLW.HOME_NO AND FLW.MEMBER_NO = #{currentMemberNo}
WHERE H.MEMBER_NO = #{memberNo}

SELECT * FROM ( SELECT G.GUESTBOOK_NO, G.GUESTBOOK_CONTENT, G.CREATE_DATE, G.IS_DEL, G.MEMBER_NO, G.HOME_NO, M.MEMBER_NICK, M.STATUS, H.PROFILE_CHANGE_NAME, NVL(LIKE_CNT.LIKE_COUNT, 0) AS LIKE_COUNT,
CASE WHEN GL.MEMBER_NO IS NOT NULL THEN 1 ELSE 0 END AS IS_LIKED,
ROWNUM AS RNUM FROM ( SELECT G.GUESTBOOK_NO, G.GUESTBOOK_CONTENT, G.CREATE_DATE, G.IS_DEL, G.MEMBER_NO, G.HOME_NO FROM TB_GUESTBOOK G WHERE G.HOME_NO = #{homeNo} AND G.IS_DEL = 'N' ORDER BY G.CREATE_DATE DESC ) G LEFT JOIN TB_MEMBER M ON G.MEMBER_NO = M.MEMBER_NO LEFT JOIN TB_MEMBER_HOME H ON M.MEMBER_NO = H.MEMBER_NO LEFT JOIN ( SELECT GUESTBOOK_NO, COUNT(*) AS LIKE_COUNT FROM TB_GUESTBOOK_LIKE GROUP BY GUESTBOOK_NO ) LIKE_CNT ON G.GUESTBOOK_NO = LIKE_CNT.GUESTBOOK_NO
LEFT JOIN TB_GUESTBOOK_LIKE GL ON G.GUESTBOOK_NO = GL.GUESTBOOK_NO AND GL.MEMBER_NO = #{currentMemberNo}
WHERE ROWNUM <= #{offset} + #{limit} ) WHERE RNUM > #{offset}

mapper.update:
UPDATE TB_GUESTBOOK
SET IS_DEL = 'Y'
WHERE GUESTBOOK_NO = #{guestbookNo}

mapper.insert:
INSERT INTO TB_GUESTBOOK_LIKE (
	MEMBER_NO,
	GUESTBOOK_NO
) VALUES (
	#{memberNo},
	#{guestbookNo}
)

mapper.delete:
DELETE FROM TB_GUESTBOOK_LIKE
WHERE MEMBER_NO = #{memberNo}
AND GUESTBOOK_NO = #{guestbookNo}

mapper.select:
SELECT COUNT(*)
FROM TB_GUESTBOOK_LIKE
WHERE MEMBER_NO = #{memberNo}
AND GUESTBOOK_NO = #{guestbookNo}

mapper.update:
UPDATE TB_MEMBER_HOME
SET PROFILE_ORIGIN_NAME = #{profileOriginName},
	PROFILE_CHANGE_NAME = #{profileChangeName}
WHERE MEMBER_NO = #{memberNo}

UPDATE TB_MEMBER_HOME
SET STATUS_MSG = #{statusMsg}
WHERE MEMBER_NO = #{memberNo}

UPDATE TB_MEMBER_HOME
SET IS_PRIVATE_PROFILE = #{isPrivateProfile}
WHERE MEMBER_NO = #{memberNo}
